local localStyle = {
    ['mbox'] = {
        ['display'] = 'flex',
        ['position'] = 'relative',
        ['border'] = '1px solid #d6d6d6',
        ['border-left-width'] = '8px',
        ['border-left-color'] = '#d6d6d6',
        ['border-radius'] = '3px',
        ['margin-bottom'] = '5px',
        ['min-height'] = '32px'
    },
    ['mbox__content'] = {
        ['display'] = 'table',
        ['box-sizing'] = 'border-box',
        ['width'] = '100%',
        ['padding'] = '8px 15px'
    },
    ['mbox__content__image'] = {
        ['display'] = 'table-cell',
        ['width'] = '40px',
        ['height'] = '100%',
        ['text-align'] = 'center',
        ['vertical-align'] = 'middle',
        ['padding-right'] = '15px'
    },
    ['mbox__content__wrapper'] = {
        ['display'] = 'table-cell',
        ['vertical-align'] = 'middle'
    },
    ['mbox__content__header'] = {
        ['display'] = 'block',
        ['font-weight'] = 'bold'
    },
    ['mbox__content__text'] = {
        ['display'] = 'block'
    },
    ['mbox__content__text__comment'] = {
        ['font-size'] = 'small'
    },
    ['mbox__content__aside'] = {
        ['display'] = 'table-cell',
        ['width'] = '100px',
        ['vertical-align'] = 'middle',
        ['text-align'] = 'center',
        ['padding-left'] = '15px',
        ['border-left'] = '1px solid #d6d6d6'
    },
    ['mbox__close'] = {
        ['position'] = 'absolute',
        ['right'] = '0',
        ['top'] = '0',
        ['padding'] = '2px 7px',
        ['font-weight'] = 'bold',
        ['font-size'] = '16px',
        ['color'] = '#bbb',
        ['cursor'] = 'pointer',
        ['transition'] = 'all .15s ease-in'
    }
}
